import json
import os
from PIL import Image

def iou(box1, box2):

    # This function takes in the coordinates of two boxes, and perform the
    # Intersection over union calculation to get the IOU scores.
    x1, y1, x2, y2 = box1
    x3, y3, x4, y4 = box2

    x_left = max(x1, x3)
    y_top = max(y1, y3)
    x_right = min(x2, x4)
    y_bottom = min(y2, y4)

    if x_right < x_left or y_bottom < y_top:
        return 0

    intersection = (x_right - x_left) * (y_bottom - y_top)
    box1_area = (x2 - x1) * (y2 - y1)
    box2_area = (x4 - x3) * (y4 - y3)
    union = box1_area + box2_area - intersection

    return intersection / union


def calculate_iou_scores_v2(json_file_path, ground_truth, image_sizes):

    # This function takes in the json_file_path which is the dictionary
    # containing all the data from the localizer, and also the ground truth
    # dictionary and a dictionary of all the image size

    with open(json_file_path, 'r') as json_file:
        localizer_data = json.load(json_file)

    print(localizer_data)

    iou_scores = []

    for gt_key, gt_boxes in ground_truth.items():
        localizer_key = gt_key.split('.')[0]
        if localizer_key not in localizer_data:
            continue

        if gt_key not in image_sizes:
            print(f"Image size for {gt_key} not found. Skipping.")
            continue

        localizer_boxes = localizer_data[localizer_key]
        image_size = image_sizes[gt_key]
        localizer_boxes_abs = [
            (x1 * image_size[0], y1 * image_size[1], x2 * image_size[0], y2 * image_size[1])
            for x1, y1, x2, y2 in localizer_boxes
        ]
        gt_boxes_abs = [
            (x1, y1, x2, y2)
            for x1, y1, x2, y2 in gt_boxes
        ]

        iou_matrix = [
            [iou(gt_box, localizer_box) for localizer_box in localizer_boxes_abs]
            for gt_box in gt_boxes_abs
        ]
        print(iou_matrix)
        max_iou_for_gt_boxes = [max(row) for row in iou_matrix]
        iou_scores.append(max_iou_for_gt_boxes)

    return iou_scores


def get_image_sizes(image_folder):
    
    # This function takes in the image folder path, and calculate the orignal image size
    image_sizes = {}

    for filename in os.listdir(image_folder):
        if filename.endswith(".jpg") or filename.endswith(".jpeg") or filename.endswith(".png"):
            image_path = os.path.join(image_folder, filename)
            try:
                with Image.open(image_path) as img:
                    width, height = img.size
                    image_sizes[filename] = (width, height)
            except Exception as e:
                print(f"Error reading image {image_path}: {e}")

    return image_sizes

